var calc_output = [];
//all conversions 1:1, no need to call UOMSYSTEM, and weight functions by itself
var sex = parseFloat(sex)
var age = parseFloat(age)
var weight = parseFloat(weight)
var height = parseFloat(height)
var tbw
var unit

if (UOMSYSTEM == false) {
	unit = 'mmol'
}
else {
	unit = 'mEq'
}

if (age == 1) { // adult
	var age_no = parseFloat(age_no)
    if (sex == 0) { // female
    	tbw = -2.097 + 0.1069 * height + 0.2466 * weight
    } 
    else { // male
		tbw = 2.447 - 0.09516 * age_no + 0.1074 * height + 0.3362 * weight
    }
}
else if (age == 0) { // child
	tbw = 0.6 * weight; 
	} 

var v = tbw * (parseFloat(desired_sodium) - parseFloat(sodium));

calc_output.push({ 
	name: 'mini', 
	value: v.toFixed(1), 
	value_text: unit, 
	message: 'Sodium Deficit' 
});
calc_output.push({ 
	name: '95_Sodium Deficit in Hyponatremia_result', 
	value: v.toFixed(1), 
	value_text: unit, 
	message: 'Sodium Deficit' 
});
